mapper.select:
SELECT 

FROM mod_times a

WHERE a.id = #{id}

SELECT 

FROM mod_times a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.comid LIKE 
			'%'||#{comid}||'%'
			'%'+#{comid}+'%'
			concat('%',#{comid},'%')


		AND a.times = #{times}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_times a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_times SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
