mapper.select:
SELECT 

FROM sys_modelement a

WHERE a.id = #{id}

SELECT
FROM sys_modelement a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.elementname LIKE '%'||#{elementname}||'%' '%'+#{elementname}+'%' concat('%',#{elementname},'%')
AND a.pdfelementname LIKE '%'||#{pdfelementname}||'%' '%'+#{pdfelementname}+'%' concat('%',#{pdfelementname},'%')
AND a.company_id = #{company.id}
AND a.office_id = #{office.id}
AND a.use_flag = '1'
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM sys_modelement a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.elementname LIKE '%'||#{elementname}||'%' '%'+#{elementname}+'%' concat('%',#{elementname},'%')
AND a.pdfelementname LIKE '%'||#{pdfelementname}||'%' '%'+#{pdfelementname}+'%' concat('%',#{pdfelementname},'%')
AND a.company_id = #{company.id}
AND a.office_id = #{office.id} AND a.use_flag = '1'
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM sys_modelement a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.elementname LIKE '%'||#{elementname}||'%' '%'+#{elementname}+'%' concat('%',#{elementname},'%')
AND a.pdfelementname LIKE '%'||#{pdfelementname}||'%' '%'+#{pdfelementname}+'%' concat('%',#{pdfelementname},'%')
AND a.company_id = #{company.id}
AND a.office_id = #{office.id}
AND a.use_flag = '0'
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_modelement a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_modelement SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
